$(document).ready(function()
{
    $(window).bind("beforeunload", function() {
        loading();
    });
});
$(function() {
    $.each($('#collapsibleNavId ul li a'), function(index, link) {
        if (link.href == document.URL) {
            $(this).addClass('active');
        }
    });
    $.each($('#collapsibleNavId ul li.dropdown'), function(index1, link1) {
        var menu = $(this).find('a.dropdown-toggle');
        $.each($(this).find('div.dropdown-menu'), function(index2, link2) {
            $.each($(this).find('a'), function(indexInArray, valueOfElement) {
                if(menu.attr('menu-data') == $(valueOfElement).attr('menu-data') && valueOfElement.href == document.URL){
                    $(menu).addClass('active');    
                }
            });
        });
    });
    typedJS();
});
function typedJS() {
    "use strict";
    var options = {
        strings: $(".element").attr('data-elements').split(','),
        typeSpeed: 100,
        backDelay: 300,
        backSpeed: 10,
        loop: true
    };
    var typed = new Typed(".element", options);
}